mapper.select:
select id,`name`,`value`,state,parent_id parentId,remark,date_format(create_time,'%Y-%m-%d %H:%i:%s') createTime from dict_info


		and state in

			${state}



		and `name`=#{param.name}


		and `name` like concat('%',#{param.name},'%')


		and parent_id=#{param.parentId}


		and parent_id=(select id from dict_info where `name`=#{param.parentName})


order by order_index

mapper.delete:
delete from dict_info where 


	name=#{name}


	parent_id=#{parentId}

mapper.update:
update dict_info


		state=#{set.state},


		`name`=#{set.name},


		`value`=#{set.value},


		`remark`=#{set.remark},


		`parent_id`=#{set.parentId},


 where


	`name`=#{param.name}


	id=#{param.id}


	parent_id=#{param.parentId}
