mapper.select:
select id,`name`,`value` from dict_info where state=1

	and `name`=#{param.name}


	and `name` like concat('%',#{param.name},'%')


	and parent_id=#{param.parentId}


	and parent_id=(select id from dict_info where `name`=#{param.parentName})

order by order_index

mapper.delete:
delete from dict_info where 


	name=#{name}


	parent_id=#{parentId}

mapper.update:
update dict_info set


		state=#{set.state},


		`name`=#{set.name},


		`value`=#{set.value},


		`remark`=#{set.remark},


 where


	`name`=#{param.name}


	id=#{param.name}


	parent_id=#{param.parentId}
